import React from 'react';
import { StyledContainer } from './styles';
import {
    Events,
    animateScroll as scroll,
    scrollSpy
  } from "react-scroll";
  
type Props = {
    children: React.ReactNode;
    HeaderText: string;
    customstyle: string;
}

class Container extends React.Component<any, Props>{
    constructor(props: Props | Readonly<Props>){
        super(props);
        this.scrollToTop = this.scrollToTop.bind(this);
    }

    componentDidMount() {
        Events.scrollEvent.register("begin", function() {
        console.log("begin", arguments);
        });

        Events.scrollEvent.register("end", function() {
        console.log("end", arguments);
        });        
        window.addEventListener('scroll', this.handleScroll);

        scrollSpy.update();
    }

    scrollToTop() {
        scroll.scrollToTop();
    }

    scrollToBottom() {
      scroll.scrollToBottom();
    }
    
    scrollTo(target: number) {
        scroll.scrollTo(target);
    }

    scrollMore(){
      scroll.scrollMore(100);
    }

    handleSetActive(to: string){
      console.log(to);
    }

    componentWillUnmount() {
        Events.scrollEvent.remove("begin");
        Events.scrollEvent.remove("end");
        window.removeEventListener('scroll', this.handleScroll);
    }

    scrollToNextSection() {
      scroll.scrollTo(100);
    }    

    handleScroll = () => { 
        console.log(scrollSpy.currentPositionY);   
        this.scrollTo(scrollSpy.currentPositionY.arguments);
    }
    render(){
        return(
            <StyledContainer onScroll={() => this.handleScroll}>
                {this.props.children}
            </StyledContainer>
        )
    }
}

export default Container;
