import Section from '../../components/section';
import { useEffect, useState } from 'react';
import axios from 'axios';

function Certifications(){
    const [files, setCertifications] = useState<[string]>(['']);

    useEffect(() => {
        const fetchData = async () => {
            const {data} = await axios.get("/api/certifications");
            setCertifications(data);
            console.log(data);
        }
        fetchData();
        return () => {
            console.log(files);
        };
    }, [files]) 
    console.log(files);
    if(files){
    return(
        <>
            <Section elementName="Certifications" HeaderText='Certification' customstyle="background-color: var(--primary);">
            <ul className="certifications">
                {files.map((file) =>  
                    <li>
                        <div className="certification">
                            <div className="file-name">
                                <div>{file}</div>
                            </div>    
                            <embed src={file} width="100px" height="200px" />
                        </div> 
                    </li>
                )}
            </ul>
            </Section>
        </>
    )}else{
        return(
            <>
                <Section elementName="Certifications" HeaderText='Certification' customstyle="background-color: var(--primary);"><></></Section>
            </>
        )
    }
}

export default Certifications;
