import React from 'react';
import { StyledContainer } from './styles';
import {
    Events,
    animateScroll as scroll,
    scrollSpy
  } from "react-scroll";
import ScrollSnap from 'scroll-snap';
import { createRef } from 'react';
  
type Props = {
    children: React.ReactNode;
    HeaderText: string;
    customstyle: string;
}

function callBack() {
    console.log('snapped')    
}

class Container extends React.Component<any, Props>{
    private container = createRef<HTMLElement>();
    constructor(props: Props | Readonly<Props>){
        super(props);
        this.scrollToTop = this.scrollToTop.bind(this);
    }

    bindScrollSnap(){
        const element = this.container.current;
        if (element){
            const snapElement = new ScrollSnap(element, {
                snapDestinationY: '90%',
            });
            snapElement.bind(callBack);
        }
    };

    componentDidMount() {
        Events.scrollEvent.register("begin", function() {
        console.log("begin", arguments);
        });

        Events.scrollEvent.register("end", function() {
        console.log("end", arguments);
        });        
        window.addEventListener('scroll', this.handleScroll);

        scrollSpy.update();
        this.bindScrollSnap();
    }

    scrollToTop() {
        scroll.scrollToTop();
    }

    scrollToBottom() {
      scroll.scrollToBottom();
    }
    
    scrollTo(target: number) {
        scroll.scrollTo(target);
    }

    scrollMore(){
      scroll.scrollMore(100);
    }

    handleSetActive(to: string){
      console.log(to);
    }

    componentWillUnmount() {
        Events.scrollEvent.remove("begin");
        Events.scrollEvent.remove("end");
        window.removeEventListener('scroll', this.handleScroll);
    }

    scrollToNextSection() {
      scroll.scrollTo(100);
    }    

    handleScroll(){ 
        Events.scrollEvent.register("scroll", function() {
            console.log(scrollSpy.currentPositionY);
        });           
    }
    render(){
        return(
            <StyledContainer id="container" ref={this.container as React.RefObject<HTMLDivElement>} onScroll={() => this.handleScroll}>
                {this.props.children}
            </StyledContainer>
        )
    }
}

export default Container;
