import styled from 'styled-components';

export const Main = styled.main`{  
    background-color: var(--black);
    color: var(--grayLight);
    scroll-snap-type: y mandatory;
    overflow-y: scroll;
    //scroll-snap-stop: always;
    scroll-behavior: smooth;
}
`;
