import React from 'react';
import {StyledMenu, StyledNavMenu} from './styles';
import {NavBtn, NavBtnLink} from '../button';
import {Link} from 'react-scroll';
import useWindowDimensions from '../getWindowDimension';
import { StyledSandWichButton } from '../button/styles';

function Menu(){ 
    const {height} = useWindowDimensions();

    function viewHeigthToPx(distance: number) {
        return (distance/100) * height;
    };
    return(
        <StyledMenu>
            <Link
                activeClass="active"
                to="Home"
                spy={true}
                smooth={true}
                duration={500}
                offset={-viewHeigthToPx(10)}
            >
                <NavBtn>Oscar Worell Filho</NavBtn> 
            </Link> 
            <StyledSandWichButton/>
            <StyledNavMenu>               
                <Link
                    activeClass="active"
                    to="AboutMe"
                    spy={true}
                    smooth={true}
                    duration={500}
                    offset={-viewHeigthToPx(10)}> 
                    <NavBtn>About me</NavBtn>
                </Link>             
                <Link
                        activeClass="active"
                        to="Experience"
                        spy={true}
                        smooth={true}
                        duration={500}
                        offset={-viewHeigthToPx(10)}>
                    <NavBtn>Career</NavBtn>
                </Link>            
                <Link
                    activeClass="active"
                    to="Contact"
                    spy={true}
                    smooth={true}
                    duration={500}
                    offset={-viewHeigthToPx(10)}>
                    <NavBtn>Contact</NavBtn>
                </Link>
                <a href="https://github.com/Worell97/Certificados">
                    <NavBtn>Certification</NavBtn>
                </a>
            </StyledNavMenu> 
            <a href="https://github.com/Worell97">
                <NavBtnLink caption="Github"/>
            </a>  
        </StyledMenu>
    ); 
}

export default Menu;
